mapper.update:
UPDATE esp_courses_point
SET courses_point =courses_point+#{CoursesPoint}
WHERE courses_id=#{coursesId} AND user_id=#{userId}

UPDATE esp_courses_point
SET courses_point =courses_point-#{CoursesPoint}
WHERE courses_id=#{coursesId} AND user_id=#{userId}

mapper.select:
SELECT courses_reward_point
FROM esp_reward_point
WHERE user_id=#{userId} and courses_id=#{coursesId}
